// Smart Question Generator with Spaced Repetition (v2 - Fixed for new users)
const vocabulary = $input.item(0).json;
const wordHistoryItems = $input.item(1)?.json || [];
const userId = $('Telegram Trigger').item.json.message.chat.id;
const userName = $('Telegram Trigger').item.json.message.chat.first_name;

// Create word history map for quick lookup
const historyMap = {};
if (Array.isArray(wordHistoryItems)) {
  wordHistoryItems.forEach(record => {
    historyMap[record.englishWord] = {
      correct: parseInt(record.correct) || 0,
      incorrect: parseInt(record.incorrect) || 0,
      lastSeen: record.lastSeen || null,
      streak: parseInt(record.streak) || 0
    };
  });
}

// Calculate priority score for each word (higher = more priority)
const scoredWords = vocabulary.map(word => {
  const history = historyMap[word.initialText] || { correct: 0, incorrect: 0, lastSeen: null, streak: 0 };

  let score = 100; // Base score

  // Factor 1: Never seen words get high priority
  if (!history.lastSeen) {
    score += 50;
  }

  // Factor 2: Incorrect answers increase priority
  score += history.incorrect * 30;

  // Factor 3: Low streak words get more priority
  if (history.streak < 3) {
    score += (3 - history.streak) * 20;
  }

  // Factor 4: Time since last seen (more time = higher priority)
  if (history.lastSeen) {
    const daysSince = (Date.now() - new Date(history.lastSeen).getTime()) / (1000 * 60 * 60 * 24);
    if (daysSince < 1) score -= 40; // Seen today? Lower priority
    else if (daysSince < 3) score -= 20; // Seen in last 3 days? Moderate penalty
    else score += daysSince * 5; // Older = higher priority
  }

  // Factor 5: Success rate (lower = higher priority)
  const total = history.correct + history.incorrect;
  if (total > 0) {
    const successRate = history.correct / total;
    score += (1 - successRate) * 40;
  }

  return {
    ...word,
    score: score,
    history: history
  };
});

