const topWords = scorredWords.slice(0, Math.max(5, Math.floor(scorredWords.length * 0.2)));

// Randomly select from top priority words
const selectedWord = topWords[Math.floor(Math.random() * topWords.length)];

// Select 3 random incorrect options
const incorrectOptions = vocabulary
  .filter(w => w.translatedText !== selectedWord.translatedText)
  .sort(() => Math.random() - 0.5)
  .slice(0, 3)
  .map(w => w.translatedText);

// Combine and shuffle all options
const allOptions = [...incorrectOptions, selectedWord.translatedText];
const shuffledOptions = allOptions.sort(() => Math.random() - 0.5);

// Find correct answer index
const correctIndex = shuffledOptions.indexOf(selectedWord.translatedText);
const correctLetter = ['A', 'B', 'C', 'D'][correctIndex];

// Determine exercise type (randomize for variety)
const exerciseTypes = ['mcq_en_to_tr', 'mcq_tr_to_en'];
const exerciseType = exerciseTypes[Math.floor(Math.random() * exerciseTypes.length)];

let questionText, options;

if (exerciseType === 'mcq_en_to_tr') {
  // English to Turkish
  questionText = `What is the correct Turkish translation for "${selectedWord.initialText}"?`;
  options = shuffledOptions;
} else {
  // Turkish to English (reverse)
  const incorrectEnOptions = vocabulary
    .filter(w => w.initialText !== selectedWord.initialText)
    .sort(() => Math.random() - 0.5)
    .slice(0, 3)
    .map(w => w.initialText);

  const allEnOptions = [...incorrectEnOptions, selectedWord.initialText];
  const shuffledEnOptions = allEnOptions.sort(() => Math.random() - 0.5);
  const correctEnIndex = shuffledEnOptions.indexOf(selectedWord.initialText);
  const correctEnLetter = ['A', 'B', 'C', 'D'][correctEnIndex];

  questionText = `What is the correct English translation for "${selectedWord.translatedText}"?`;
  options = shuffledEnOptions;

  return [{
    json: {
      userId: userId,
      userName: userName,
      question: questionText,
      options: options,
      correctAnswer: correctEnLetter,
      correctWord: selectedWord.initialText,
      turkishWord: selectedWord.translatedText,
      englishWord: selectedWord.initialText,
      exerciseType: exerciseType,
      wordScore: selectedWord.score,
      wordHistory: selectedWord.history,
      formattedQuestion: `${questionText}\n\nA) ${options[0]}\nB) ${options[1]}\nC) ${options[2]}\nD) ${options[3]}\n\nReply with A, B, C, or D`
    }
  }];
}

return [{
