// Smart Question Generator - Optimized for SQLite
const words = $input.all().map(item => item.json);
const userId = $('Telegram Trigger').item.json.message.chat.id;
const userName = $('Telegram Trigger').item.json.message.chat.first_name || 'User';

if (words.length === 0) {
  return [{
    json: {
      error: true,
      message: "No vocabulary found. Please run init_database.js first!"
    }
  }];
}

// Words are already sorted by priority, pick from top 20%
const topCount = Math.max(5, Math.ceil(words.length * 0.2));
const topWords = words.slice(0, topCount);

// Randomly select from top priority words
const selectedWord = topWords[Math.floor(Math.random() * topWords.length)];

// Determine exercise type
const exerciseTypes = ['mcq_en_to_tr', 'mcq_tr_to_en'];
const exerciseType = exerciseTypes[Math.floor(Math.random() * exerciseTypes.length)];

let questionText, correctAnswer, options;

if (exerciseType === 'mcq_en_to_tr') {
  // English to Turkish
  questionText = `What is the correct Turkish translation for "${selectedWord.english_word}"?`;

  // Get 3 incorrect Turkish options
  const incorrectOptions = words
    .filter(w => w.turkish_word !== selectedWord.turkish_word)
    .sort(() => Math.random() - 0.5)
    .slice(0, 3)
    .map(w => w.turkish_word);

  // Combine and shuffle
  const allOptions = [...incorrectOptions, selectedWord.turkish_word];
  options = allOptions.sort(() => Math.random() - 0.5);

  correctAnswer = options.indexOf(selectedWord.turkish_word);

} else {
  // Turkish to English
  questionText = `What is the correct English translation for "${selectedWord.turkish_word}"?`;

  // Get 3 incorrect English options
  const incorrectOptions = words
    .filter(w => w.english_word !== selectedWord.english_word)
    .sort(() => Math.random() - 0.5)
    .slice(0, 3)
    .map(w => w.english_word);

  // Combine and shuffle
  const allOptions = [...incorrectOptions, selectedWord.english_word];
  options = allOptions.sort(() => Math.random() - 0.5);

  correctAnswer = options.indexOf(selectedWord.english_word);
}

const correctLetter = ['A', 'B', 'C', 'D'][correctAnswer];

const formattedQuestion = `${questionText}\n\nA) ${options[0]}\nB) ${options[1]}\nC) ${options[2]}\nD) ${options[3]}\n\nğŸ“ Reply with A, B, C, or D`;

return [{
  json: {
    userId: userId,
    userName: userName,
    wordId: selectedWord.id,
    question: questionText,
    options: options,
    correctAnswer: correctLetter,
    correctIndex: correctAnswer,
    englishWord: selectedWord.english_word,
    turkishWord: selectedWord.turkish_word,
    exerciseType: exerciseType,
    priorityScore: selectedWord.priority_score,
    formattedQuestion: formattedQuestion
  }
}];

